import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const RewardHistory = () => {
  const { id } = useParams();
  const [rewardsHistory, setRewardsHistory] = useState([]);
  const [rewardsBalance, setRewardsBalance] = useState(0);

  useEffect(() => {
    axios.get(`/api/rewards/${id}/rewards`)
      .then(response => setRewardsHistory(response.data))
      .catch(error => console.log(error));
    axios.get(`/api/users/${id}`)
      .then(response => setRewardsBalance(response.data.rewardsBalance))
      .catch(error => console.log(error));
  }, [id]);

  return (
    <div>
      <h1>Rewards History</h1>
      <p>Rewards Balance: {rewardsBalance}</p>
      <table>
        <thead>
          <tr>
            <th>#</th>
            <th>Date-Time</th>
            <th>Rewards Received</th>
            <th>User Name</th>
          </tr>
        </thead>
        <tbody>
          {rewardsHistory.map((reward, index) => (
            <tr key={reward._id}>
              <td>{index + 1}</td>
              <td>{new Date(reward.timestamp).toLocaleString()}</td>
              <td>{reward.points}</td>
              <td>{reward.givenBy.name}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default RewardHistory;
