import React, { useState, useEffect } from 'react';
import { useParams, useHistory } from 'react-router-dom';
import axios from 'axios';

const NewReward = () => {
  const { id } = useParams();
  const history = useHistory();
  const [points, setPoints] = useState(0);
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState('');
  const [p5Balance, setP5Balance] = useState(0);

  useEffect(() => {
    axios.get('/api/users')
      .then(response => setUsers(response.data.filter(user => user._id !== id)))
      .catch(error => console.log(error));
    axios.get(`/api/users/${id}`)
      .then(response => setP5Balance(response.data.p5Balance))
      .catch(error => console.log(error));
  }, [id]);

  const handleSubmit = (event) => {
    event.preventDefault();
    if (points > 100 || points > p5Balance) return;
    axios.post('/api/rewards', { points, givenBy: id, givenTo: selectedUser })
      .then(() => history.push(`/${id}/p5`))
      .catch(error => console.log(error));
  };

  return (
    <div>
      <h1>New Reward</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Points</label>
          <input
            type="number"
            value={points}
            onChange={e => setPoints(Number(e.target.value))}
          />
        </div>
        <div>
          <label>User</label>
          <select value={selectedUser} onChange={e => setSelectedUser(e.target.value)}>
            <option value="">Select User</option>
            {users.map(user => (
              <option key={user._id} value={user._id}>{user.name}</option>
            ))}
          </select>
        </div>
        <button type="submit" disabled={points > 100 || points > p5Balance}>Submit</button>
        <button type="button" onClick={() => history.push(`/${id}/p5`)}>Cancel</button>
      </form>
    </div>
  );
};

export default NewReward;
